workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;

class MasterListReportController
{
    @Binding
    def binding

    @Service("ReportParameterService")
    def svcParams;

    @Service('RPTReportService') 
    def svc 

    @Service('DateService')
    def dateSvc

    def year 
    def reportdata
    def readonly = true 

    void init() {
        year = dateSvc.serverYear 
    }

    def preview() {
        generateReport()
        return 'preview' 
    }

    void generateReport() {
        reportdata = svc.generateMasterList( year )
        report.viewReport()
    }

    void print(){
        generateReport()
        ReportUtil.print( report.report, true )
    }

    def report = [
        getReportName : { return 'masterlist' },
        getReportData : { return reportdata },
        getParameters : { 
            return svcParams.getStandardParameter( OsirisContext.env.USERNAME )
        }
    ] as ServerReportModel;

    def back() {
        return 'default' 
    }
}
