workunit.code:
import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*

public class rpt_transmittal_mgmtController 
{
    @Binding 
    def binding

    @Service('RPTTransmittalService')
    def svc 

    def selectedItem
    def searchText

    void search() {
        listHandler.load()
    }

    def listRefreshHandler = {
        listHandler.load()
    }

    def create() {
        return InvokerUtil.lookupOpener('rpt_transmittal.create', [listRefreshHandler:listRefreshHandler] )
    }

    def open(){
        return InvokerUtil.lookupOpener('rpt_transmittal.open', [entity:selectedItem] )
    }

    def listHandler = [
        getRows     : { 
            return 50 
        },
        getColumns  : { return [
            new Column(name:'txnno', caption:'Transmittal No.', maxWidth:120),
            new Column(name:'txndate', caption:'Date Transmitted', type:'date', maxWidth:120),
            new Column(name:'createdby', caption:'Transmitted By'),
            new Column(name:'lguname', caption:'Transmitting LGU'),
        ]},
        fetchList  : { 
            return svc.getList( searchText, it ) 
        },
    ] as PageListModel
}
